import { useState } from 'react';

import TitleBar from "./components/TitleBar";
import ResultBar from "./components/ResultBar";
import ColorsContainer from "./components/ColorsContainer";
import titleStyle from './components/TitleBar.module.css';


function App(props) {

  const [messageContainerData, setMessageContainerData] = useState("");
  const [playAgainContainerData, setplayAgainContainerData] = useState("");
  const [titleColorContainerData, setTitleColorContainerData] = useState("");
  const [playAgainFlagData, setplayAgainFlagData] = useState();

  let message = messageContainerData;
  let buttonText = playAgainContainerData;
  let titleColorApp = titleColorContainerData;
  let playAgainFlag = playAgainFlagData;

  return (
    <div>
      <h1>
        We are using {process.env.REACT_APP_DATABASE}
      </h1>
      <TitleBar color={props.color} titleColor={titleColorApp} />
      <ResultBar message={message} buttonText={buttonText} playAgain={setplayAgainFlagData} />
      <ColorsContainer messageContainer={setMessageContainerData} playAgainContainer={setplayAgainContainerData} titleColorContainer={setTitleColorContainerData} color={props.color} correctBtn={props.correctBtn} />
    </div>
  );
}

export default App;
