import { useState } from 'react';

import ColorButton from './ColorButton';
import colorStyle from './ColorsContainer.module.css';

function ColorsContainer(props) {

    const [messageButtonData, setMessageButtonData] = useState("");

    let lotteryArray = ['btn1', 'btn2', 'btn3', 'btn4', 'btn5', 'btn6'];
    let correctBtn = lotteryArray[Math.floor(Math.random() * 7)];
    while (typeof correctBtn == 'undefined') {
        correctBtn = lotteryArray[Math.floor(Math.random() * 7)];
    }
    console.log("Correct: " + correctBtn);


    props.messageContainer(messageButtonData);
    console.log("CONTAINER: " + messageButtonData);

    return (
        <div className={colorStyle.mainContainer}>
            <div className={colorStyle.upperRow}>
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn1" />
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn2" />
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn3" />
            </div>
            <div className={colorStyle.lowerRow}>
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn4" />
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn5" />
                <ColorButton messageButton={setMessageButtonData} correctBtn={correctBtn} name="btn6" />
            </div>
        </div>
    );
}

export default ColorsContainer;
