import { useState } from 'react';

import ColorButton from './ColorButton';
import colorStyle from './ColorsContainer.module.css';

function ColorsContainer(props) {

    const [messageButtonData, setMessageButtonData] = useState("");
    const [playAgainButtonData, setPlayAgainButtonData] = useState("");

    props.messageContainer(messageButtonData);
    props.playAgainContainer(playAgainButtonData);

    const a = 10;
    const ShowA = () => <div>{a}</div>;
    const MultipleA = () => <div>{a * a}</div>;


    return (
        <div className={colorStyle.mainContainer}>
            <div className={colorStyle.upperRow}>
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn1" />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn2" />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn3" />
            </div>
            <div className={colorStyle.lowerRow}>
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn4" />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn5" />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} color={props.color} name="btn6" />
            </div>
        </div>
    );
}

export default ColorsContainer;
