import containerStyle from './ColorsContainer.module.css';
import ColorSquare from './ColorSquare';

function ColorsContainer({ colorsArray, correctBtn, setTitleBarColor, setResultBarButtonText,setResultBarMessage }) {

    const clickHandler = (color, id) => {
        if (color === correctBtn) {
            changeColor()
            setTitleBarColor(correctBtn)
            props.setResultBarButtonText("PLAY AGAIN")
            props.setResultBarMessage("CORRECT!")
        } else {
            changeVisibility(id)
            props.setResultBarMessage("WRONG, TRY AGAIN!")
        }

    }

    const changeColor = () => {
        const tempColorsArray = [...colorsArray]
        tempColorsArray.map(object => {
            if (object.visibility === 'visible') {
                object.color = correctBtn
            }

        })
        props.setColorsArray(tempColorsArray)
    }

    const changeVisibility = (id) => {
        const tempColorsArray = [...colorsArray]
        tempColorsArray[id].visibility = 'hidden';
        props.setColorsArray(tempColorsArray)
    }

    return (
        <div className={containerStyle.mainContainer}>
            <div className={containerStyle.innerContainer}>
                {Object.keys(colorsArray).map((key) => {
                    return (
                        <ColorSquare key={colorsArray[key].id}
                            correctBtn={correctBtn}
                            onClick={() => clickHandler(colorsArray[key].color, props.colorsArray[key].id)}
                            backgroundColor={props.colorsArray[key].color}
                            visibility={props.colorsArray[key].visibility}
                        />
                    )
                })}
            </div>
        </div>
    )


}

export default ColorsContainer;
