import { useState } from 'react';

import ColorButton from './ColorButton';
import colorStyle from './ColorsContainer.module.css';

function ColorsContainer(props) {

    const [messageButtonData, setMessageButtonData] = useState("Select the correct color.");
    const [playAgainButtonData, setPlayAgainButtonData] = useState("NEW COLORS");
    const [titleColorButtonData, setTitleColorButtonData] = useState("rgb(92, 138, 138)");
    const [allButtonsColor, setAllButtonsColor] = useState("");

    props.messageContainer(messageButtonData);
    props.playAgainContainer(playAgainButtonData);
    props.titleColorContainer(titleColorButtonData);

    console.log(titleColorButtonData);
    console.log("Buttons Color")

    return (
        <div className={colorStyle.mainContainer}>
            <div className={colorStyle.upperRow}>
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn1" style={{ 'backgroundColor': allButtonsColor }} />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn2" style={{ 'backgroundColor': allButtonsColor }} />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn3" style={{ 'backgroundColor': allButtonsColor }} />
            </div>
            <div className={colorStyle.lowerRow}>
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn4" style={{ 'backgroundColor': allButtonsColor }} />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn5" style={{ 'backgroundColor': allButtonsColor }} />
                <ColorButton playAgainButton={setPlayAgainButtonData} messageButton={setMessageButtonData} titleColorButton={setTitleColorButtonData} allButtons={setAllButtonsColor} color={props.color} correctBtn={props.correctBtn} name="btn6" style={{ 'backgroundColor': allButtonsColor }} />
            </div>
        </div>
    );
}

export default ColorsContainer;
