import { useState } from 'react';

import ColorButton from './ColorButton';
import colorStyle from './ColorsContainer.module.css';

function ColorsContainer() {

    const [color1, setColor1] = useState(randomColorGenerator);
    const [color2, setColor2] = useState(randomColorGenerator);
    const [color3, setColor3] = useState(randomColorGenerator);
    const [color4, setColor4] = useState(randomColorGenerator);
    const [color5, setColor5] = useState(randomColorGenerator);
    const [color6, setColor6] = useState(randomColorGenerator);


    function randomColorGenerator() {
        let red = Math.floor(Math.random() * 256);                         // Setting a random value (0-255) for the red.
        let green = Math.floor(Math.random() * 256);                       // Setting a random value (0-255) for the green.
        let blue = Math.floor(Math.random() * 256);                        // Setting a random value (0-255) for the blue.

        return "rgb(" + red + ", " + green + ", " + blue + ")";            // Returning the whole new rgb(r,g,b) string.
    }

    return (
        <div className={colorStyle.mainContainer}>
            <div className={colorStyle.upperRow}>
                <ColorButton name="btn1" style={{ backgroundColor: color1 }} />
                <ColorButton name="btn2" style={{ backgroundColor: color2 }} />
                <ColorButton name="btn3" style={{ backgroundColor: color3 }} />
            </div>
            <div className={colorStyle.lowerRow}>
                <ColorButton name="btn4" style={{ 'background-color': color4 }} />
                <ColorButton name="btn5" style={{ 'background-color': color5 }} />
                <ColorButton name="btn6" style={{ 'background-color': color6 }} />
            </div>
        </div>
    );
}

export default ColorsContainer;
